import logging

logging.basicConfig(filename='error.log', level=logging.INFO)

def log_errors(func):
    def wrapper(*args, **kwargs):
        try:
            result = func(*args, **kwargs)
            logging.info(f"Function {func.__name__} called with args={args}, {kwargs}. Result: {result}")
            return result
        except Exception as e:
            logging.error(f"Function {func.__name__} raised error: {e}")
    return wrapper

@log_errors
def example_function(a, b):
    try:
        return a / b
    except ZeroDivisionError:
        raise ValueError("–î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å!")
```

